mapper.select:
SELECT partner_order_id, partner_user_id, item_name, quantity, total_amount, vat_amount, tax_free_amount, product_code, tid
FROM Payment
WHERE product_code = #{productCode}

mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO payment (partner_order_id, partner_user_id, item_name, quantity, total_amount, vat_amount, tax_free_amount, product_code, tid)
VALUES (#{partnerOrderId}, #{partnerUserId}, #{itemName}, #{quantity}, #{totalAmount}, #{vatAmount}, #{taxFreeAmount}, #{productCode}, #{tid});
